import {useState} from "react";

const Game = () => {
    const [guess, setGuess] = useState("")


    const handleInput = (e) => {
        setGuess(e.target.value)
    }
    return (
        <div>
            <h2>Угадай число от 0 до 10</h2>
            <input type="number" onChange={handleInput}/>
            <button>Check</button>
            <button>Reset</button>
        </div>
    )
}

export default Game
